import * as C from './styles'
import {Theme} from '../../components/Theme/intex'
import {FormActions, useForm} from '../../context/FormContext'
import {useEffect} from 'react'
import {ReactComponent as CheckIcon} from '../../svgs/check.svg'
import {useHistory} from 'react-router-dom'

export const FormStep4 = () => {
    const {state, dispatch} = useForm()
    const history = useHistory()

    useEffect(() => {
        if (state.name === '') {
            history.push('/')
        } else {
            dispatch({
                type: FormActions.setCurrentStep,
                payload: 4
            })
        }
    }, [])

    return (
        <Theme>
            <C.Container>
                <h2>You'll pay 2500 PKR total</h2>
                <p>or 475 PKR in Tax alone</p>
                <p>or 2025 PKR (Tax not included)</p>
                <p className='check-email'>Enviamos um e-mail para: <b>{state.email}</b> com a confirmação do cadastro
                </p>
            </C.Container>
        </Theme>
    )
}
