import * as C from './styles'
import {ReactChild, ReactFragment, ReactPortal, useEffect, useState} from "react";

export const Header = () => {
    const [data, setData] = useState([]);

    const getData = () => {
        fetch('https://free.currconv.com/api/v7/convert?q=USD_PKR&compact=ultra&apiKey=2de3ebe521651a795386'
            , {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            }
        )
            .then(function (response) {
                console.log(response)
                return response.json();
            })
            .then(function (myJson) {
                console.log(myJson);
                setData(myJson)
            });
    }


    useEffect(() => {
        getData()
    }, [])

    return (
        <C.Container>
            <h1 style={{textAlign: "center"}}>How much VAT will you pay?</h1>
            <p style={{textAlign: "center"}}>The simple calculator for how much Value Added Tax you will pay when
                importing items to Pakistan.</p>
            {
                data && data.length > 0 && data.map((item: { about: boolean | ReactChild | ReactFragment | ReactPortal | null | undefined; }) =>
                    <p>{item.about}</p>)
            }
        </C.Container>
    )
}
