import styled from 'styled-components'

export const Container = styled.div`
  padding: 30px;

  P {
    display: block;
  }

  h2 {
    display: block;
    margin-bottom: 5px;
    color: rgb(1, 63, 28);
  }

  .passo {
    margin-bottom: 20px;
  }

  button {
    cursor: pointer;
    width: 120px;
    height: 50px;
    border-radius: 10px;
    background-color: rgb(1, 63, 28);
    color: whitesmoke;
    border: none;
    margin-top: 20px;

  }

  a {
    padding: 0 15px 0 15px;
    text-decoration: none;
    color: rgb(1, 63, 28);
  }

`
