import styled from 'styled-components'

export const Container = styled.div`
  padding: 40px;

  P {
    display: block;
    font-size: 25px;
    color: rgb(1, 63, 28);
  }

  table {
    border-spacing: 5px;
    display: table;
    table-layout: fixed;
    width: 100%;
  }

  table div {
    display: table-cell;
  }

  img {
    width: 100%;
  }

  h2 {
    display: block;
    margin-bottom: 5px;
    color: rgb(1, 63, 28);
    font-size: 35px;
  }

  .check-email {
    font-size: 15px;
  }


`

export const IconArea = styled.div`
  margin: 30px;


`
