import * as C from './styles'
import {Theme} from '../../components/Theme/intex'
import {FormActions, useForm} from '../../context/FormContext'
import {useEffect} from 'react'
import {ReactComponent as CheckIcon} from '../../svgs/check.svg'
import {useHistory} from 'react-router-dom'
import { SocialIcon } from 'react-social-icons';

export const FormStep4 = () => {
    const {state, dispatch} = useForm()
    const history = useHistory()

    useEffect(() => {
        if (state.name === '') {
            history.push('/')
        } else {
            dispatch({
                type: FormActions.setCurrentStep,
                payload: 4
            })
        }
    }, [])

    return (
        <Theme>
            <C.Container>
                <h2>You'll pay 2500 PKR total</h2>
                <p style={{margin:3}}>or 475 PKR in Tax alone</p>
                <p style={{margin:3}}>or 2025 PKR (Tax not included)</p>
                <h4 style={{marginTop:10, color:'rgb(1, 63, 28)'}}>Share results with friends:</h4>
                <SocialIcon url="https://www.example.com" />

            </C.Container>
        </Theme>
    )
}
